package com.randomusers.albertsonsrandomuserslist.presenter.screens.mainimport kotlinx.coroutines.flow.StateFlowimport com.randomusers.albertsonsrandomuserslist.data.model.UsersModelinterface MainContract {    interface ViewModel {        val uiState: StateFlow<UIState>        fun onEventDispatcher(intent: Intent)    }    data class UIState(        val userList: List<UsersModel> = emptyList(), // Holds the list of UsersModel    )    // Updated Intent interface with LoadUsers    sealed interface Intent {        data class MoveToDetail(val usersModel: UsersModel) : Intent        data class LoadUsers(val count: Int) : Intent // Add LoadUsers intent to accept user count    }}